/**
 * Donation Employer Checkbox Module
 * Handles the "no employer" checkbox functionality for donation v2 forms
 */


var handleCheckboxChange = function (targetCheckbox) {
  const employerFieldSelectors = [
    { selector: '[id^="donation_employer"]', required: true },
    { selector: '[id^="donation_occupation"]', required: true },
    { selector: '[id^="donation_work_address_address1"]', required: true },
    { selector: '[id^="donation_work_address_address2"]', required: false },
    { selector: '[id^="donation_work_address_address3"]', required: false },
    { selector: '[id^="donation_work_address_city"]', required: true },
    {
      selector: '[id^="donation_work_address_state"]',
      required: true,
      displayConditions: [{ key: 'country_code', values: ['US', 'CA'] }],
    },
    { selector: '[id^="donation_work_address_zip"]', required: true },
    { selector: '[id^="donation_work_address_country_code"]', required: true },
  ];

  const displayConditionsSatisfied = (field, displayConditions) => {
    const form = field.closest('form');
    return displayConditions.every((condition) => {
      const displayConditionsField = form.querySelector(
        `[id^="donation_work_address_${condition.key}"]`
      );
      const displayConditionsValue = displayConditionsField.value;

      return condition.values.includes(displayConditionsValue);
    });
  };

  const hideAndDisableFieldBySelector = (selector) => {
    const field = document.querySelector(selector);
    if (!field || field.disabled) return;
    const formGroup = field.closest('div');
    if (formGroup) formGroup.style.display = 'none';
    field.removeAttribute('required');
  };

  const showAndEnableFieldBySelector = (
    selector,
    required,
    displayConditions = []
  ) => {
    const field = document.querySelector(selector);
    if (!field || field.disabled) return;
    if (!displayConditionsSatisfied(field, displayConditions)) return;

    const formGroup = field.closest('div');
    if (formGroup) formGroup.style.display = '';
    if (required) field.setAttribute('required', 'required');
  };

  if (targetCheckbox.checked === true) {
    // Hide employer and occupation fields
    employerFieldSelectors.forEach(({ selector }) => {
      hideAndDisableFieldBySelector(selector);
    });
  } else {
    // Show employer and occupation fields
    employerFieldSelectors.forEach(
      ({ selector, required, displayConditions }) => {
        showAndEnableFieldBySelector(selector, required, displayConditions);
      }
    );
  }
};

































