        // look for custom data attribute and if it is present on the page, do not inject the link
        const skipInjectingLink = document.querySelector('[data-skip-injecting-link="true"]');
        // look to see if the link is already on the page
        const isAlreadyInjected = document.querySelector('a[href$="/supporter_portal/security"]');
        // only inject if neither of the above is true
        const injectLink = !isAlreadyInjected && !skipInjectingLink;
