    /**
     * Searches for all elements in the DOM that have a data attribute of "truncate" and adds the truncate plugin to
     * them
     * @method truncateStrings
     * @public
     * @type void
     */
    truncateStrings: function () {
      var $stringsToTruncate = $('[data-truncate]');

      if ($stringsToTruncate.length && $().truncate) {
        var truncateLength = $stringsToTruncate.data('truncate');

        if (truncateLength !== undefined) {
          $stringsToTruncate.truncate({
            max_length: Number(truncateLength)
          });
        }
      }
    },

